INSERT INTO `ky_options` VALUES ('317', '_transient_feed_d677f38b04483417c6b9854f1bc8afc0', 'a:4:{s:5:\"child\";a:1:{s:0:\"\";a:1:{s:3:\"rss\";a:1:{i:0;a:6:{s:4:\"data\";s:3:\"\n\n\n\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:7:\"version\";s:3:\"2.0\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:1:{s:7:\"channel\";a:1:{i:0;a:6:{s:4:\"data\";s:51:\"\n	\n	\n	\n	\n	\n	\n		\n		\n	\n	\n		\n		\n		\n		\n		\n		\n		\n		\n		\n	\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:3:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:17:\"Cryout Creations \";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"http://www.cryoutcreations.eu\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:51:\"WordPress and Opencart Themes, Modules and Plugins!\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:13:\"lastBuildDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 28 Oct 2014 13:17:14 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"language\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"en-GB\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"generator\";a:1:{i:0;a:5:{s:4:\"data\";s:27:\"http://wordpress.org/?v=4.0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"item\";a:10:{i:0;a:6:{s:4:\"data\";s:60:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:39:\"Windows Phone support pitch-in campaign\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:73:\"http://www.cryoutcreations.eu/news/windows-phone-support-pitchin-campaign\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:82:\"http://www.cryoutcreations.eu/news/windows-phone-support-pitchin-campaign#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 29 Sep 2014 12:34:32 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:7:{i:0;a:5:{s:4:\"data\";s:6:\"Mantra\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:4:\"News\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:7:\"Nirvana\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:8:\"Parabola\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:7:\"Tempera\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:5;a:5:{s:4:\"data\";s:8:\"pitch-in\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:6;a:5:{s:4:\"data\";s:13:\"windows phone\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:38:\"http://www.cryoutcreations.eu/?p=27095\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:844:\"You may have noticed the appearance of a little blue box on the right side of our website. It\'s not there just because we love blue, but because we need a bit of help (being blue).\r\n\r\nDeveloping web-related creations is hard work enough. Developing free creations... that\'s turning more and more into a spiritual path. But when we also need to test our creations, that\'s where matters complicate.\r\n\r\nIn today\'s world with thousands upon thousands of devices, each with its own software, testing things just gets more difficult. While an emulator is good enough in some cases, it does not cover everything. We need an actual device running Windows Phone to truly test things out. We need the real deal... <a href=\"http://www.cryoutcreations.eu/news/windows-phone-support-pitchin-campaign\">Read the rest <span class=\"meta-nav\">&#8594; </span></a>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:3:\"Zed\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:3229:\"<p>You may have noticed the appearance of a little blue box on the right side of our website. It&#8217;s not there just because we love blue, but because we need a bit of help (being blue).</p>\n<p>Developing web-related creations is hard work enough. Developing free creations&#8230; that&#8217;s turning more and more into a spiritual path (hence our &#8211; perhaps you noticed &#8211; interest in deities). But when we also need to test our creations, that&#8217;s where matters complicate (and deities don&#8217;t seem to want to help).</p>\n<p>In today&#8217;s world with thousands upon thousands of devices, each with its own software deciding whether or not it should follow the standards, testing things just gets more difficult. Beside 4 computers (each with all mentionable browsers and operating systems), 5 Android devices of various sizes and 2 iThings, lately Windows Phone (thank you, Microsoft&#8230;) came into play. While an emulator is good enough in some cases (and we did use it to its limits to test basic functionality), it does not cover everything. We need an actual device running Windows Phone to truly test things out. We need the real deal.</p>\n<p>Here is where the little blue box that started this whole monologue comes in. It keeps track of the donations we received in our &#8220;helps us improve Windows Phone support&#8221; campaign (thanks to the handy little script <a href=\"http://pitchinbox.com/\" target=\"_blank\">PitchinBox</a> provides). If you&#8217;re using a Windows phone or want your site running a theme of ours to better handle itself on a Windows phone, you can donate any amount to the cause. Just click on the &#8220;Pitch in&#8221; button, enter the amount (use any name you wish) and then you&#8217;ll get to the usual Paypal donation system. While we do love that lucky 13% we&#8217;re at so far, we do need all your help to take over all the <del>world</del> browsers.</p>\n<p>We&#8217;ll use every bit gathered until the end of October on <em>[nope, not on Steam\'s Halloween sale, not this time at least; but on]</em> an entry level Windows device to do our proper testing on.</p>\n<p>And if we don&#8217;t reach the desired target&#8230; well.. we&#8217;ll just have to spend all your generous donations on coffee<a title=\"or hot chocolate\" href=\"#\">*</a> to help us stay awake in those long testing sessions using the emulator&#8230;</p>\n<div id=\"attachment_27468\" style=\"width: 460px\" class=\"wp-caption aligncenter\"><a href=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/windows_phone_z.jpg\"><img class=\"wp-image-27468\" src=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/windows_phone_z.jpg\" alt=\"\" width=\"450\" height=\"273\" /></a><p class=\"wp-caption-text\">Part of the testing hardware (Windows-es &amp; Android)</p></div>\n<div id=\"attachment_27271\" style=\"width: 460px\" class=\"wp-caption aligncenter\"><a href=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/windows_phone_k.jpg\"><img class=\"wp-image-27271\" src=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/windows_phone_k.jpg\" alt=\"Part 2\" width=\"450\" height=\"265\" /></a><p class=\"wp-caption-text\">Another part of the testing hardware (more Windows, Android &amp; iOS)</p></div>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:78:\"http://www.cryoutcreations.eu/news/windows-phone-support-pitchin-campaign/feed\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:1;a:6:{s:4:\"data\";s:60:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:45:\"Nirvana is now LIVE and you are a part of it!\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:76:\"http://www.cryoutcreations.eu/wordpress-themes/nirvana/nirvana-now-live-part\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:85:\"http://www.cryoutcreations.eu/wordpress-themes/nirvana/nirvana-now-live-part#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 22 Sep 2014 19:12:44 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:7:{i:0;a:5:{s:4:\"data\";s:6:\"Mantra\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:7:\"Nirvana\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:8:\"Parabola\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:7:\"Tempera\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:7:\"nirvana\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:5;a:5:{s:4:\"data\";s:5:\"theme\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:6;a:5:{s:4:\"data\";s:9:\"wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:38:\"http://www.cryoutcreations.eu/?p=27206\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:431:\"Our latest WordPress theme, Nirvana, is now Live and available to download and install directly from WordPress.org. Now there\'s absolutely no reason not to check it out.\r\n\r\nFollow us after the jump for the celebration and the full changelog since the theme\'s brief but intense existence. <a href=\"http://www.cryoutcreations.eu/wordpress-themes/nirvana/nirvana-now-live-part\">Read the rest <span class=\"meta-nav\">&#8594; </span></a>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:3:\"Kay\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:7475:\"<p><a href=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/dystopia.jpg\"><img class=\"aligncenter size-full wp-image-27295\" src=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/dystopia.jpg\" alt=\"dystopia\" width=\"768\" height=\"494\" /></a></p>\n<p>At first it was not only strange but also incredibly difficult to manage. Being thrust from the cold, industrial world I knew into <em>this</em> was something well beyond anything I could ever adjust to. Still glass giants, buzzing metallic machines and steel skies were all I had ever known. Spending hours in a poorly lit cubicle, squinting at the bright panel in front of me, pushing buttons, inputting data, calculating and extracting other data were pretty much the only things I ever knew. Life was as bleached and tedious on the inside as it was on the outside. But that was all I had ever known.</p>\n<p><a href=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/corridors.jpg\"><img class=\"alignright size-full wp-image-27294\" src=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/corridors.jpg\" alt=\"corridors\" width=\"373\" height=\"500\" /></a></p>\n<p>The corporate giant I worked in was filled with series upon series of empty corridors with doors on each side, all leading to more corridors with their own emptiness and even more doors. And under the corridors’ overly bright, constantly flickering neon lights the illusion of choice was so great that I believed it. Working overtime, struggling to make my way upward, to advance through the concrete bowels of this all-powerful colossus gave a fake sense of accomplishment. And it was all I had so I gave it my all.</p>\n<p>But one bright cold day in April, returning from my short launch break I found a note placed discreetly on my desk. It just said: <em>‘When ready, go to the other side’</em>. I sat down, hands in my lap, holding the piece of paper between my fingers while cautiously looking over my shoulders for anything suspicious. But everything was still. So I focused on the note once again, trying to figure out this riddle and guess who it was from. Recent conversations with colleagues replayed in my head as I flipped the note, only to find something else was written on the other side: <em>‘cryoutcreations.com’</em>. So this was where I had to go. Curiosity got the best of me so I awoke my dormant computing machine and swiftly typed the mysterious address. Upon activation everything went black. And then it happened.</p>\n<p><a href=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/note.jpg\"><img class=\"aligncenter size-full wp-image-27297\" src=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/note.jpg\" alt=\"note\" width=\"448\" height=\"299\" /></a></p>\n<p>I woke up almost hovering over a grassy plain, wide valleys stretching out before me and foaming streams filling the gorges between them. In the distance mountains with sharp, white tops were reaching for the sky, scratching the few patches of fluffy clouds on its belly. A soft eddy gave speed to the countless birds and insects and also bent, slowly, the trees and flowers on one side then on the other. Everything was alive; I was alive. I knew then what this place must be so I stopped reasoning and questioning how and why I got here. I simply was.</p>\n<p><a href=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/nirvana-place.jpg\"><img class=\"aligncenter size-full wp-image-27296\" src=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/nirvana-place.jpg\" alt=\"nirvana-place\" width=\"768\" height=\"480\" /></a></p>\n<p>I soon realized that the hovering sensation I was experiencing was just my new state of being. I had no more worries, no more stress, no more regret. And so I was weightless, free, I was one with the world around me. And by being a part of the whole, I became complete. I laid back, every muscle in my body relaxed and I fell asleep&#8230;</p>\n<p><a href=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/apples.jpg\"><img class=\"alignleft size-full wp-image-27293\" src=\"http://www.cryoutcreations.eu/wp-content/uploads/2014/09/apples.jpg\" alt=\"apples\" width=\"261\" height=\"300\" /></a></p>\n<p>&nbsp;</p>\n<p>But a short while after, a tinkling sensation in my stomach woke me up. To my surprise, I was hungry. I really didn’t think I could still be hungry in this place. I got up and took another look around: rivers, plants, forests, I could even see other humans in the distance. This wasn’t the end. This was a new beginning, a beginning I wasn’t really prepared for. I couldn’t cook, hunt, fish nor build a shelter, I couldn’t really do anything. But I would learn, by myself or from the others here and I’d make it work. I had choices, real choices that would have an immediate effect on the world around me. It wasn’t going to be easy, but things would go my way for once. This was my world now- Nirvana, and I was free.</p>\n<p>&nbsp;</p>\n<p>And the straight forward version of this post: Our latest WordPress theme, Nirvana, is now Live and available to <a href=\"https://wordpress.org/themes/nirvana\" title=\"Install Nirvana\" target=\"_blank\"><strong>download and install</strong></a> directly from WordPress.org. The changelog (also bundled with the theme files as <em>changelog.txt</em>) for the past versions follows:</p>\n<p><span style=\"text-decoration: underline;\">Nirvana 0.9.5.1</span></p>\n<ul>\n<li>Fixed styles enqueue order bug created in 0.9.5</li>\n</ul>\n<p><span style=\"text-decoration: underline;\">Nirvana 0.9.5</span></p>\n<ul>\n<li>Removed conditional check for &#8216;wp_enqueue_media&#8217;</li>\n<li>Removed all &#8216;wp_register_style&#8217; and &#8216;wp_register_script&#8217; functions.</li>\n<li>Enqueued all admin scripts and styles via &#8216;admin_enqueue_scripts&#8217;</li>\n<li>Added a changelog.txt file</li>\n</ul>\n<p><span style=\"text-decoration: underline;\">Nirvana 0.9.4</span></p>\n<ul>\n<li>Adjusted columns (photo frames, padding, margins)</li>\n<li>Fixed multi-line widget titles</li>\n<li>Removed leftover presentation page &#8216;hide background&#8217; option</li>\n<li>Default option for columns is now frameless</li>\n<li>Fixed &#8216;more posts&#8217; button border on IE</li>\n<li>Fixed sidebar &#8216;dasboard&#8217; typo</li>\n</ul>\n<p><span style=\"text-decoration: underline;\">Nirvana 0.9.3</span></p>\n<ul>\n<li>Edited &#8216;readme.txt&#8217; for new and improved copyright declarations</li>\n<li>Removed Bebas Neue font (copyright incompatibility)</li>\n<li>Removed hardcoded Javascript found in frontpage.php &#8211; moved it to custom-styles.php</li>\n<li>Replaced &#8216;dirname ( __FILE__ )&#8217; with &#8216;get_template_directory()&#8217;</li>\n</ul>\n<p><span style=\"text-decoration: underline;\">Nirvana 0.9.2</span></p>\n<ul>\n<li>Fixed header image responsiveness for full width / normal width</li>\n<li>Fixed header image responsiveness</li>\n<li>Removed leftovers from defaults.php</li>\n<li>Added Russian translation</li>\n</ul>\n<p><span style=\"text-decoration: underline;\">Nirvana 0.9.1</span></p>\n<ul>\n<li>Fixed menu alignment</li>\n<li>Fixed header image not starting at top:0</li>\n<li>Fixed topbar height when set to &#8216;fixed&#8217;</li>\n<li>Fixed topbar on mobile res below 480px</li>\n<li>Fixed front columns layout when using custom widgets</li>\n<li>Fixed Presentation Page columns on mobile using custom widgets (nth-child replaced with nth-of-type)</li>\n</ul>\n<p>Stay tuned for more updates, we have big plans for this one!</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:81:\"http://www.cryoutcreations.eu/wordpress-themes/nirvana/nirvana-now-live-part/feed\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:2;a:6:{s:4:\"data\";s:63:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"WordPress Child Themes Tutorial\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:89:\"http://www.cryoutcreations.eu/wordpress-themes/wordpress-tutorials/wordpress-child-themes\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:98:\"http://www.cryoutcreations.eu/wordpress-themes/wordpress-tutorials/wordpress-child-themes#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 13 Aug 2014 14:08:27 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:8:{i:0;a:5:{s:4:\"data\";s:6:\"Mantra\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:8:\"Parabola\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:7:\"Tempera\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:19:\"Wordpress Tutorials\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:11:\"child theme\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:5;a:5:{s:4:\"data\";s:5:\"theme\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:6;a:5:{s:4:\"data\";s:8:\"tutorial\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:7;a:5:{s:4:\"data\";s:9:\"wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:37:\"http://www.cryoutcreations.eu/?p=3508\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:397:\"You never really got a solid grip on WordPress child themes? Well now\'s your chance.\r\n\r\nWe provide both a complete step-by-step tutorial as well as a one page go-to resource for quick starting  a child theme in no time. The choice is yours. <a href=\"http://www.cryoutcreations.eu/wordpress-themes/wordpress-tutorials/wordpress-child-themes\">Read the rest <span class=\"meta-nav\">&#8594; </span></a>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:3:\"Kay\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:4581:\"<p><a href=\"http://www.cryoutcreations.eu/wp-content/uploads/2013/09/wordpres-child-themes5.jpg\"><img class=\"aligncenter size-full wp-image-26718 no-border-img\" src=\"http://www.cryoutcreations.eu/wp-content/uploads/2013/09/wordpres-child-themes5.jpg\" alt=\"WordPress Child Themes Tutorial\" width=\"720\" height=\"450\" /></a></p>\n<h2><strong>Do you need a child theme?</strong></h2>\n<p>If you&#8217;re not satisfied with the customization options that the WordPress theme you&#8217;re using provides then you have to take matters into your own hands. Even if that theme gives you hundreds of options (like our themes do) there&#8217;s still something which probably doesn&#8217;t get your boat floating smoothly enough.</p>\n<p>Or maybe you&#8217;re the extra creative type that really needs to make everything his own and is never satisfied. Or maybe you&#8217;re a serial killer, coming from a broken family, with a long history of drug and alcohol abuse and you&#8217;re just starting your first blog about hand-crafted torture equipment and we all know how picky serial killers can get. Too specific? Let&#8217;s move on.</p>\n<h2><strong>Why do you need a child theme?</strong></h2>\n<p>Yes, you can edit theme files directly, but then, when you make an update you&#8217;ll lose all your customizations and blame the theme developers for it. Or, option no. 2: you can choose to never update the theme after you make your changes. But that way you lose all future bug-fixes and extra features that come with future theme updates. Not to mention that one day you might completely forget about your customizations and hit that &#8216;Update&#8217; button without giving it a second thought. Who are you going to blame then, ha? Ha? You&#8217;ll need a mirror for this one.</p>\n<p>This is why it&#8217;s better to never touch theme files and unconditionally love and embrace WordPress&#8217; child themes. It may take 5 minutes out of your life now but it will save you a lot more in the long run.</p>\n<h2><strong>Can you handle <del>the truth</del> a child theme?</strong></h2>\n<p>If you want to get things done your way, if you want the ultimate customization power at your fingertips you will have to go the extra mile. Don&#8217;t worry, it&#8217;s not an actual mile nor a kilometre &#8211; it&#8217;s more like a couple of yards/meters and we&#8217;re here to walk that distance with you. Multiple times if we have to.</p>\n<p>Here&#8217;s what you&#8217;ll need:</p>\n<ul>\n<li>As you&#8217;ll have to add a few files to your server, you&#8217;ll need FTP access to the server and a FTP client (we&#8217;ll suggest one). This is recommended but not 100% necessary because after creating the child theme folder on your local machine you can archive that and upload it via WordPress (<a title=\"Semi-manual installation\" href=\"www.cryoutcreations.eu/wordpress-themes/wordpress-tutorials/how-to-install-a-wordpress-theme\" target=\"_blank\">semi-manual installation</a>). More on this later.</li>\n<li>You&#8217;ll also need a text editor to edit those files (we&#8217;ll suggest one as well)</li>\n<li>Basic understanding of CSS and maybe HTML and PHP. But these can be learned as you go especially since you&#8217;ll be copy-pasting and editing, not creating things from scratch.</li>\n<li>A couple of minutes out of your busy blogging schedule</li>\n</ul>\n<p>So basically you don&#8217;t have to know anything and if child themes sound just like what the doctor ordered and you want to get a solid grip on them, you are welcomed to move on to page 2 of our tutorial. It will take you on a step-by-step journey through WordPress child themes at the end of which you will have a solid understanding of anything child theme related.</p>\n<p>If, on the other hand, you just want to create a child theme as soon as possible, we&#8217;ve also provided a WordPress Child Theme cheat sheet. It&#8217;s a one page go-to resource of everything you need when creating a child themes. So, what will it be? The blue pill or the red one? (actual color names have been changed to protect the identity of those involved)</p>\n<div class=\"button-choice\"><a class=\"cr2-button-color\" target=\"_parent\" style=\"background-color:#098080\" href=\"http://www.cryoutcreations.eu/wordpress-themes/tempera/wordpress-child-themes/2\" title=\"Child Theme Tutorial\" >Child Theme Tutorial</a>   <a class=\"cr2-button-color\" target=\"_parent\" style=\"background-color:#FF7210\" href=\"http://www.cryoutcreations.eu/wordpress-themes/tempera/wordpress-child-themes/5\" title=\"Child Theme Cheat Sheet\" >Child Theme Cheat Sheet</a></div>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:94:\"http://www.cryoutcreations.eu/wordpress-themes/wordpress-tutorials/wordpress-child-themes/feed\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"19\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:3;a:6:{s:4:\"data\";s:51:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:27:\"Mantra 2.3.4 still breathes\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:81:\"http://www.cryoutcreations.eu/wordpress-themes/mantra/mantra-2-3-4-still-breathes\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:90:\"http://www.cryoutcreations.eu/wordpress-themes/mantra/mantra-2-3-4-still-breathes#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 18 Apr 2014 16:08:45 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:4:{i:0;a:5:{s:4:\"data\";s:6:\"Mantra\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:6:\"mantra\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:5:\"theme\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:9:\"wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:38:\"http://www.cryoutcreations.eu/?p=24878\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:453:\"Yes, it is still alive! Despite such a long break in releases (the longest break yet), Mantra still has some breaths left.\r\n\r\nOne of these breaths is the 2.3.4 update, bringing the eagerly awaited (and fiercely requested) social icons bugfix (bug kindly bundled by yours truly with the previous update).  <a href=\"http://www.cryoutcreations.eu/wordpress-themes/mantra/mantra-2-3-4-still-breathes\">Read the rest <span class=\"meta-nav\">&#8594; </span></a>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:3:\"Zed\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:960:\"<p>Yes, it is still alive! Despite such a long break in releases (the longest break yet), Mantra still has some breaths left.</p>\n<p>One of these breaths is the 2.3.4 update, bringing the eagerly awaited (and fiercely requested) social icons bugfix (bug kindly bundled by yours truly with the previous update).</p>\n<p>Changelog:</p>\n<ul>\n<li>fixed the social icons (unable to disable) bug introduced in 2.3.3</li>\n<li>corrected the meta show/hide options not working for custom post types (pointed out by <a href=\"http://wordpress.org/support/topic/removing-category-indcation-from-post-preview-1\" target=\"_blank\">tkemmere</a>)</li>\n<li>corrected content editor (<em>html .mceContentBody</em>) width to properly use the configured site width instead of an arbitrary number</li>\n<li>improved handling of empty site title and/or description (will no longer display a single dash in the browser title)</li>\n<li>updated French translation</li>\n</ul>\n<p>&nbsp;</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:86:\"http://www.cryoutcreations.eu/wordpress-themes/mantra/mantra-2-3-4-still-breathes/feed\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"42\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:4;a:6:{s:4:\"data\";s:51:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:26:\"Mantra 2.3.3 stays in line\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:80:\"http://www.cryoutcreations.eu/wordpress-themes/mantra/mantra-2-3-3-stays-in-line\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:89:\"http://www.cryoutcreations.eu/wordpress-themes/mantra/mantra-2-3-3-stays-in-line#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Sun, 02 Feb 2014 13:39:22 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:4:{i:0;a:5:{s:4:\"data\";s:6:\"Mantra\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:6:\"mantra\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:6:\"themes\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:9:\"wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:38:\"http://www.cryoutcreations.eu/?p=21107\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:517:\"Mantra receives a small maintenance update, tweaking and changing things here and there.\r\n\r\nThe most noteworthy thing related to the 2.3.3 update (but not part of the update itself) is the corrupt Mantra archive lurking around on Wordpress\' servers. One of its evil purposes is to leave your pages content-less (but be ready for other nasty surprises as well). The fix? <a href=\"http://www.cryoutcreations.eu/wordpress-themes/mantra/mantra-2-3-3-stays-in-line\">Read the rest <span class=\"meta-nav\">&#8594; </span></a>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:3:\"Zed\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:1811:\"<p>Mantra receives a small maintenance update, tweaking and changing things here and there.</p>\n<p><del>The most noteworthy thing related to the 2.3.3 update (but not part of the update itself) is the corrupt Mantra archive lurking around on WordPress&#8217; servers. It hides there in the dark waiting to jump out and break your site. One of its evil purposes is to leave your pages content-less (but be ready for other nasty surprises as well).</del></p>\n<p><del>The fix? Well since the problem is on WordPress&#8217; servers, our power is limited. What we can do is recommend you to create an export of Mantra&#8217;s settings, delete it and attempt to reinstall it. If the issue re-occurs, delete again and try a manual install using the theme archive available <a href=\"http://img.cryout.eu/mantra.2.3.3.zip\" target=\"_blank\">here</a>.</del></p>\n<p>Edit: WordPress fixed the archive and nobody should receive a corrupt file on future updates to this version.</p>\n<p>Changelog:</p>\n<ul>\n<li>updated to the new WP 3.8 &#8220;fluid-layout&#8221; and &#8220;fixed-layout&#8221; tags</li>\n<li>3 social icons are now enabled by default in two theme areas</li>\n<li>fixed import/export settings not working on some rare occasions</li>\n<li>hopefully fixed smileys getting huge in captions</li>\n<li>changed default table cell alignment to top (instead of bottom)</li>\n<li>fixed Google fonts to correctly handle SSL websites</li>\n<li>added Recaptcha, Math Captcha, Captcha compatibility styling (thanks to <a href=\"http://www.cryoutcreations.eu/wordpress-themes/mantra/mantra-1-8-1-is-not-superman/comment-page-1#comment-13523\" target=\"_blank\">David B</a>)</li>\n<li>corrected site title not resizing enough on the smallest mobile devices</li>\n<li>updated French translation</li>\n<li>added Czech translation</li>\n</ul>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:85:\"http://www.cryoutcreations.eu/wordpress-themes/mantra/mantra-2-3-3-stays-in-line/feed\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"39\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:5;a:6:{s:4:\"data\";s:72:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"Show your WordPress theme some love\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s: